mapper.select:
select distinct color from goodsForLevels where 1=1 

	and name like #{name}


	and specification like #{specification}

select code, ifnull(${level}, 1) as price, imagepath from goodsForLevels where 1=1 

	and name like #{name}


	and specification like #{specification}


	and color like #{color}

select code, ifnull(${level}, 1) as price, imagePath from goodsForLevels where 1=1 

	and code like #{code}


	and specification like #{specification}


	and color like #{color}
